#!/bin/bash
export WONT_FIX=invalid-name,bad-indentation,trailing-newlines,wrong-import-position,fixme,c-extension-no-member,line-too-long,trailing-whitespace,bad-whitespace
export CATCHALL_MAY_FIX_LATER=no-else-continue,bad-continuation,no-value-for-parameter,too-many-arguments,too-many-locals,unused-argument,pointless-string-statement,too-many-instance-attributes,too-few-public-methods,attribute-defined-outside-init,protected-access,unused-variable,too-many-function-args,no-self-use,no-method-argument,function-redefined,too-many-public-methods,anomalous-backslash-in-string,arguments-differ,too-many-boolean-expressions,too-many-statements,no-self-argument,unnecessary-pass,not-a-mapping,multiple-statements,inconsistent-return-statements,no-else-return,too-many-nested-blocks,too-many-lines,unidiomatic-typecheck,simplifiable-if-statement,too-many-return-statements,not-callable,eval-used,empty-docstring,consider-using-enumerate,unbalanced-tuple-unpacking,global-statement,duplicate-key,no-init,redefined-argument-from-local,missing-docstring,unused-wildcard-import,invalid-unary-operand-type,wildcard-import,exec-used,bare-except,duplicate-code,unsubscriptable-object,redefined-outer-name,no-member
export PYLINT="pylint --disable=$WONT_FIX,$CATCHALL_MAY_FIX_LATER"

find . -iname "*.py" | xargs $PYLINT
